CREATE TABLE JQUERY_BOARD(POST_ID VARCHAR(50) NOT NULL,TITLE VARCHAR(50) NOT NULL,CONTENTS VARCHAR(255),REG_ID VARCHAR(50),REG_DATE VARCHAR(10),COMMUNITY_ID VARCHAR(50) NOT NULL,CONSTRAINT PK_JQUERY_BOARD PRIMARY KEY(POST_ID));
CREATE TABLE JQUERY_CATEGORY(CATEGORY_ID VARCHAR(50) NOT NULL,CATEGORY_NAME VARCHAR(50) NOT NULL,CATEGORY_DESC VARCHAR(100),REG_DATE VARCHAR(10),CONSTRAINT PK_JQUERY_CATEGORY PRIMARY KEY(CATEGORY_ID));
CREATE TABLE JQUERY_COMMUNITY(COMMUNITY_ID VARCHAR(50) NOT NULL,COMMUNITY_NAME VARCHAR(50) NOT NULL,COMMUNITY_DESC VARCHAR(100),CATEGORY_ID VARCHAR(50),REG_ID VARCHAR(50),REG_DATE VARCHAR(10),CONSTRAINT PK_JQUERY_COMMUNITY PRIMARY KEY(COMMUNITY_ID),CONSTRAINT FK_JQUERY_CATEGORY FOREIGN KEY(CATEGORY_ID) REFERENCES JQUERY_CATEGORY(CATEGORY_ID));
CREATE TABLE JQUERY_ATTACHED(ID VARCHAR(50) NOT NULL,NAME VARCHAR(255),FILE_SIZE NUMERIC(15),REF_ID VARCHAR(50) NOT NULL,CONSTRAINT PK_JQUERY_ATTACHED PRIMARY KEY(ID));


